select

    ${fields}


    *

from nideshop_goods
where 1 = 1
and is_delete = 0
and is_on_sale = 1

    and is_new = #{is_new}


    and is_hot = #{is_hot}


    and brand_id = #{brand_id}


    and 'name' like concat('%',#{keyword},'%')


    and category_id = #{category_id}


    and keywords like concat('%',#{keyword},'%')



    and category_id in

        #{item}



    and id in

        #{item}




        order by ${sidx} ${order}


        order by id desc



    limit #{offset}, #{limit}

/*  select a.*,b.id as product_id
