select a.id, a.name, a.list_pic_url, a.market_price, a.retail_price, a.goods_brief, b.id AS
product_id,count(og.id) as orderNum
from nideshop_goods a
left join nideshop_product b on a.id = b.goods_id
left join nideshop_product c on a.id = c.goods_id and c.retail_price > b.retail_price and b.id != c.id
left join nideshop_order_goods og on og.product_id = b.id

    left join nideshop_goods_group gg on gg.goods_id = a.id

where 1 = 1 and b.id is not null and c.id is null and a.is_delete = 0 and a.is_on_sale = 1

    and a.is_new = #{is_new}


    and a.is_hot = #{is_hot}


    and a.brand_id = #{brand_id}


    and a.'name' like concat('%',#{keyword},'%')


    and a.category_id = #{category_id}


    and a.keywords like concat('%',#{keyword},'%')


    and gg.id is not null


    and a.category_id in

        #{item}



    and a.id in

        #{item}


group by a.id, a.name, a.list_pic_url, a.market_price, a.retail_price, a.goods_brief, b.id


        order by ${sidx} ${order}


        order by a.id desc



    limit #{offset}, #{limit}

select
    nideshop_goods.*,nideshop_brand.name as brand_name
from nideshop_goods ,nideshop_brand
where 1 = 1 and  nideshop_goods.brand_id=nideshop_brand.id   and nideshop_goods.is_delete = 0 and nideshop_goods.is_on_sale = 1

    and nideshop_goods.is_new = #{is_new}


    and nideshop_goods.is_hot = #{is_hot}


    and nideshop_goods.brand_id = #{brand_id}


    and nideshop_goods.'name' like concat('%',#{keyword},'%')


    and nideshop_goods.category_id = #{category_id}


    and nideshop_goods.keywords like concat('%',#{keyword},'%')


    and nideshop_goods.merchant_id=#{merchantId}



    and nideshop_goods.category_id in

        #{item}



    and id in

        #{item}




        order by ${sidx} ${order}


        order by nideshop_goods.id desc



    limit #{offset}, #{limit}

